import React, { useState } from 'react';
import './style.css';

function AddButton() {
  const [name, setName] = useState(0);
  const [arys, setArys]=useState([]);
  const logs = ()=>{
    setName(name+1);
    let noneary:[]= []
    let arg = [...Array(name)].map((_,i) => i)
    Array.prototype.push.apply(noneary, arg)
    return setArys(noneary)
  }
  console.log("logs,arys",arys)
  const handleRemove = () => {
    console.log("handleRemovearys.length",arys.length)
    if(arys.length > 1){
      arys.pop()
      setArys(arys)
      setName(name-1)
    }

  }

  console.log(arys);
  const rendDivBtn = arys.map((counkey:number) => {
      return <div className="divbtn" key={counkey.toString()}>{counkey.toString()}</div>
      })

  return (
    <div>
      <h2>増えるボタン</h2>
      <div className="divbtn" onClick={logs}>{name}</div>
      <h2>減るボタン</h2>
      <div className="divbtn" onClick={handleRemove}>{name}</div>
      {rendDivBtn}
    </div>
  );
}

export default AddButton;
