import React, {useState} from 'react';
import IconButton from '@mui/material/IconButton';
import EditIcon from '@mui/icons-material/Edit';
import CheckboxList from './CheckboxList';


export interface ToDo {
  id: string;
  text: string;
}

function TodoListNext() {
  const [inputValue, setInputValue] = useState('')
  const [isOpen, setIsOpen] = useState<boolean>()
  const [todos, setTodos] = useState<ToDo[]>([]);

  const todoAdd = (text: string) => {
    setTodos( prevTodos => [
      ...prevTodos,
      { id: Math.random().toString(), text: text }
    ])
  }

  const handleClick = ()=>{
    setIsOpen(()=>!isOpen)
  }
  const todo = [{id: 'タスク１', text: 'ご飯を作る'}]

  return (
    <div>
      <button className="todoBtn" onClick={()=>{setInputValue('クリックした')}}>＋</button>
      {inputValue}
      <IconButton size="large" onClick={handleClick}><EditIcon /></IconButton>
      {isOpen ? "ON": "OFF"}
      <div className="todolistForm">
        <label className="todoformlabel">
          <input type="text" placeholder="ここに書いてね" />
        </label>
      </div>

      <div className="containerCenter" >
        <CheckboxList />
      </div>
    </div>
  );
}

export default TodoListNext;
