import React, { useState } from 'react'
import CheckboxList from './CheckboxList';
export interface TodoValue {
  id: string
  text: string
}
export interface TodoProps {
  items: {id: string, text: string}[]
};

const TodoList = () => {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState<TodoValue[]>([])

  const handleSubmit=(e:React.FormEvent)=>{
    e.preventDefault()
    setTodoValue( x => [...x,{id: Math.random().toString(), text: input}])
  }


  return (
    <div>
      <h2>TodoList</h2>
      <form onSubmit={handleSubmit}>
        <div className="todolistForm">
        <label className="todoformlabel" ><input type='text' onChange={ e => setInput(e.currentTarget.value)}/></label>
        </div>
      </form>
      <ul>
      {todoValue.map(list => {
        return(
          <li key={list.id}>{list.text}</li>
          )
        })
      }
      </ul>
      <div className="containerCenter" >
        <CheckboxList items={todoValue}/>
      </div>
    </div>
  )
}

export default TodoList
