import React, { useState } from 'react'
import CheckboxList from './CheckboxList'
export interface TodoValue {
  id: string
  text: string
}

const TodoList = () => {
  const [input,setInput] = useState('')
  const [isValidation,setIsValidation] = useState(true)
  const [todoValue, setTodoValue] = useState<TodoValue[]>([])

    const validation = (input:string) => {
    if(!input) {
      console.log("naiyo")
      setIsValidation(false)
      return;
    }
  }

  const handleSubmit=(e:React.FormEvent)=>{
    e.preventDefault()
    validation(input)
    setTodoValue( x => [...x,{id: Math.random().toString(), text: input}])
  }



  return (
    <div>
      <h2>TodoList</h2>
      <form onSubmit={handleSubmit}>
        <div className="todolistForm">
        <label className="todoformlabel" >
      {isValidation ?
          <input type='text' onChange={ e => setInput(e.currentTarget.value)} />
          :<input type='text' onChange={ e => setInput(e.currentTarget.value)} className="disabledInput"/>}
        </label>
        </div>
      </form>
      <div className="todoContainerCenter" >
        {isValidation ?<CheckboxList items={todoValue}/>:""}
      </div>
    </div>
  )
}

export default TodoList
