import React, { useState } from 'react'
import CheckboxList from './CheckboxList'
export interface TodoValue {
  id: string
  text: string
}

const TodoList = () => {
  const [input,setInput] = useState('')
  const [isValidation,setIsValidation] = useState(true)
  const [todoValue, setTodoValue] = useState<TodoValue[]>([])

  const handleChange =(e)=>{
    setInput(e.currentTarget.value)
    console.log(e)
  }

    const validation = (input:string) => {
    !input ? setIsValidation(false) :  setIsValidation(true)
    }

  const handleSubmit=(e:React.FormEvent)=>{
    e.preventDefault()
    validation(input)
    isValidation && setTodoValue( x => [...x,{id: Math.random().toString(), text: input}])
  }

  const DisabledInputText = ()=>{
    return <p className="disabledInputText">※空文字は追加できません</p>
  }

  return (
    <div>
      <h2>TodoList</h2>
      <form onSubmit={handleSubmit}>
        <div className="todolistForm">
        <label className="todoformlabel" >
      {isValidation ?
          <input type='text' onChange={(e)=> handleChange(e)} />
          :<><input type='text' onChange={ e => setInput(e.currentTarget.value)} className="disabledInput"/><DisabledInputText /></>}
        </label>
        </div>
      </form>
      <div className="todoContainerCenter" >
        {isValidation && <CheckboxList items={todoValue}/>}
      </div>
    </div>
  )
}

export default TodoList
