import React, { useState } from 'react';
import './style.css';

// export interface setTodoValue {
//   todos:[{
//     id: number,
//     text : string,
//   }]
// }

let currentId = 0

function TodoList() {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState({
    todos:[
      {
        id:0,
        text:'ここにでる',
      }
    ]
    })

  // const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
  //   setInput(e.currentTarget.value)
  // }

  const handleSubmit=(e:React.FormEvent<HTMLFormElement>)=>{
    if(currentId===0){currentId=1}
    e.preventDefault()
    // const newTodo = {id: currentId, input}
    // const newTodos =  {...todoValue.todos[0], id: currentId,text:input}
    // console.log("newTodo:", newTodos)
    setTodoValue({todos:[{...todoValue.todos[0], id: currentId,text:input}]})
    console.log("todoValue:",todoValue.todos[0])
    // alert(input);
    currentId++
  }

  return (
    <div>
      <h2>TodoList</h2>
      <ul><li>{todoValue.todos[0]['text']}</li></ul>
      <form onSubmit={handleSubmit}>
      <label><input type='text'  value={input} onChange={(e)=>{setInput(e.currentTarget.value)}}/></label>
      <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default TodoList;
