import React, { useState } from 'react';
import './style.css';

// export interface setTodoValue {
//   todos:[{
//     id: number,
//     text : string,
//   }]
// }

let currentId = 0

function TodoList() {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState({todos:[{id:0, text:'ここにでる',}]})

  // const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
  //   setInput(e.currentTarget.value)
  // }

  const handleSubmit=(e:React.FormEvent<HTMLFormElement>)=>{
    currentId++
    e.preventDefault()
    const newTodo = {id: currentId, text:input}
    const newTodos =  {...todoValue.todos, newTodo}
    // console.log("newTodo:", newTodos)
    // setTodoValue({...todoValue.todos[0], {id: currentId,text:input})
    // setTodoValue(todoValue.todos.push() )
    // alert(input);
  }
  console.log("todoValue:",todoValue)

  return (
    <div>
      <h2>TodoList</h2>
      <ul>
        {todoValue.map(({id,text})=>{<li key={id}>{text}</li>})}
        {/* <li>{todoValue['text']}</li> */}
      </ul>
      <form onSubmit={handleSubmit}>
      <label><input type='text'  value={input} onChange={(e)=>{setInput(e.currentTarget.value)}}/></label>
      <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default TodoList;
