import React, { useState } from 'react';
import './style.css';

// export interface setTodoValue {
//   todos:[{
//     id: number,
//     text : string,
//   }]
// }

let currentId = 0

function TodoList() {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState({
    todos:[
      {
        id:0,
        text:'',
      }
    ]
    })

  // const clicked =  (text:string) => {
  //   const newTodo = {todos:[{id: currentId, text}]}
  //   const newTodos =  [...todoValue.todos, newTodo.todos]
  //   setTodoValue(newTodos)
  //   console.log()
  // }

  const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.currentTarget.value)
  }

  const handleSubmit=()=>{
    // e.preventDefault();
    // console.log(e.target.value)
    const newTodo = {id: currentId, input}
    const newTodos =  {...todoValue.todos[0], newTodo}
    console.log("newTodo:", newTodos)
    setTodoValue({todos:[newTodos]})
    console.log("todoValue:",todoValue)
  }

  return (
    <div>
      <h2>TodoList</h2>
      {/* <ul><li>{todoValue.todos[0].["text7]}</li></ul> */}
      <form onSubmit={handleSubmit}></form>
      <input type='text'  value={input} onChange={handleChange}/>
      <button>アクションボタン</button>
    </div>
  );
}

export default TodoList;
