import React, { useState } from 'react';
import './style.css';

export interface TodoList{
  id: any
  text:any
}

export interface TodoValue {
    [x: string]: {
        id: any;
        text: any;
    }[];
}

function TodoList() {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState<TodoValue>({todos:[{id:0,text:''}]})
  const [currentId,setCurrentId] = useState(0)
  const [addargs, setAddargs] = useState<TodoList[]>()
  const [arys, setArys] = useState([])

  // const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
  //   setInput(e.currentTarget.value)
  // }

  const handleSubmit=(e:React.FormEvent<HTMLFormElement>)=>{
    e.preventDefault()
    setCurrentId(currentId+1)
    // let noneary:[]= []
    // let arg = [...Array(currentId)].map((_,i) => i+1)
    // Array.prototype.push.apply(noneary, arg)
    Array.prototype.push.apply(todoValuetodos,[input])
    // setArys(noneary)
    setTodoValue(todoValue)
    setAddargs([{id:currentId,text:todoValue}])
    console.log(addargs)
  }

  const handleDelete = (index:number) => {
    console.log(index)
    // delete list.todos[index]
    // console.log(addvales)
  }

  const listitem = arys.map((cou:number) => {
    addargs && addargs.map(({id,text},index) => {
            <div>
                <p key={cou.toString()}>{text}</p>
              <button key={cou.toString()} onClick={()=>handleDelete(index)}>Delete</button>
            </div>
  })
})


  return (
    <div>
      <h2>TodoList</h2>
        {listitem}
        <p>todo</p>
        <p>todo</p>
        <p>todo</p>
      <form onSubmit={handleSubmit}>
      <label><input type='text'  value={input} onChange={(e)=>{setInput(e.currentTarget.value)}}/></label>
      <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default TodoList;
