import React, { useState } from 'react';
import './style.css';

function AddButton() {
  const [name, setName] = useState(1)
  const [arys, setArys] = useState([])

  const handleAddBtn = ()=>{
    setName(name+1)
    let noneary:[]= []
    let arg = [...Array(name)].map((_,i) => i+1)
    Array.prototype.push.apply(noneary, arg)
    return setArys(noneary)
  }

  // console.log("logs,arys",arys)

  const handleRemoveBtn = () => {
    console.log("handleRemovearys.length",arys.length)
    if(arys.length > 0){
      arys.pop()
      setArys(arys)
      setName(name-1)
    }
  }

  const handleChangeValue = (e:React.ChangeEvent<HTMLInputElement>)=> {
    e.preventDefault()
      const value = Number(e.currentTarget.value)
      setName(value)
  }

  const rendDivBtn = arys.map((counkey:number) => {
      return <div className="divbtn" key={counkey.toString()}>{counkey.toString()}</div>
      })

  const handleSubmit = (e:React.FormEvent <HTMLFormElement>) => {
    e.preventDefault()
    console.log(e.target)
  }

  return (
    <div>
      <div className="handleBtnContainer">
        <div className="handleBtnContainerList">
          <h2>増えるボタン</h2>
          <div className="divbtn" onClick={handleAddBtn}>{name}</div>
        </div>
        <div className="handleBtnContainerList">
          <h2>減るボタン</h2>
          <div className="divbtn" onClick={handleRemoveBtn}>{name}</div>
        </div>
        <div className="handleBtnContainerList">
          <h2>増えるフォーム</h2>
          <form onSubmit={handleSubmit}>
          <input type="number" value={name} name="count" onChange={handleChangeValue}></input>
          <input type="submit" value="追加"></input>
          </form>
        </div>
      </div>
        <hr/>
      <div className="containerBtn">
      {rendDivBtn}
      </div>
    </div>
  );
}

export default AddButton;
