import React, { useState } from 'react';
import './style.css';

export interface TodoList{
  id:number,
  text:string
}

export interface TodoValue {
  // [currentId:number]: TodoList
    [x: number]: {
        id: number;
        text: string;
    }[];
}


function TodoList() {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState([''])
  const [currentId,setCurrentId] = useState(0)
  let products: TodoValue[]=[]
  const list = {
    [currentId]: [
      {
        id:0,
        text: "りんご"
      },
      {
        id:2,
        text: "ごりら"
      },
      {
        id:3,
        text: "らっぱ"
      },
      {
        id:4,
        text: "ぱんだ"
      },
    ]
  }

  products.push(list)
  // console.log(products[0].todos)

  // const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
  //   setInput(e.currentTarget.value)
  // }

  const handleSubmit=(e:React.FormEvent<HTMLFormElement>)=>{
    e.preventDefault()
    setCurrentId(currentId+1)
    Array.prototype.push.apply(todoValue,[input])
    setTodoValue(todoValue)
  }
  // console.log("todoValue:",todoValue.todos[0])

  const addvales = [{id:currentId,text:todoValue}]
  console.log(addvales);
  const handleDelete = (index:number) => {
    console.log(index)
    // delete list.todos[index]
    // console.log(list.todos)
  }

  function NumberList(props:any){
    const { addvales } = props
    addvales.map(({id,text},index) =>
      <div><p key={id.toString()}>{text}<button key={id.toString()} onClick={()=>handleDelete(index)}>消す</button></p></div>)
  }

  return (
    <div>
      <h2>TodoList</h2>
      <NumberList addvales="addvales"/>
        {/* <li>{todoValue['text']}</li> */}
        <p>todo</p>
        <p>todo</p>
        <p>todo</p>
      <form onSubmit={handleSubmit}>
      <label><input type='text'  value={input} onChange={(e)=>{setInput(e.currentTarget.value)}}/></label>
      <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default TodoList;
