import React, { useState } from 'react';
import './style.css';

// export interface setTodoValue {
//   todos:[{
//     id: number,
//     text : string,
//   }]
// }

let currentId = 0

function TodoList() {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState({todos:[{id:0, text:'ここにでる',}]})

  const list = {
    todos: [
      {
        id:0,
        text: "りんご"
      },
      {
        id:2,
        text: "ごりら"
      },
      {
        id:3,
        text: "らっぱ"
      },
      {
        id:4,
        text: "ぱんだ"
      },
    ]
  }

  // const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
  //   setInput(e.currentTarget.value)
  // }

  const handleSubmit=(e:React.FormEvent<HTMLFormElement>)=>{
    currentId++
    e.preventDefault()
    // const newTodo = {todos:[{id: currentId, text:input}]}
    // const newTodos =  {...todoValue.todos, newTodo}
    // console.log("newTodo:", newTodos)
    setTodoValue({todos:[{id: currentId, text:input}]})
    // setTodoValue(todoValue.todos.push() )
    // alert(input);
  }
  console.log("todoValue:",todoValue.todos[0])

  handleDelete

  return (
    <div>
      <h2>TodoList</h2>
        {list.todos.map(({id,text}) =><div><p key={id}>{text}<button onClick={handleDelete}>消す</button></p></div>)}
        {/* <li>{todoValue['text']}</li> */}
      <form onSubmit={handleSubmit}>
      <label><input type='text'  value={input} onChange={(e)=>{setInput(e.currentTarget.value)}}/></label>
      <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default TodoList;
