  return (
    <div>
      <h2>TodoList</h2>
      <form onSubmit={handleSubmit}>
        <label><input type='text' onChange={ e => setInput(e.currentTarget.value)}/></label>
        <button type="submit">Submit</button>
      </form>
      {todoValue && todoValue.map(list => {
        return(
          <ul>
            <li key={list.id}>{list.text}</li>
          </ul>
        )
      })}
    </div>
  );
}

export default TodoList
