import React, { useState } from 'react'
import CheckboxList from '@mui/material/CheckboxList';
export interface TodoValue {
  id: string
  text: string
}

interface TodoProps {
  items: {id: string, text: string}[]
};

const TodoList = () => {
  const [input,setInput] = useState('')
  const [todoValue, setTodoValue] = useState<TodoValue[]>([])
  const [propsary, setPropasAry] = useState()

  const handleSubmit=(e:React.FormEvent)=>{
    e.preventDefault()
    setTodoValue( x => [...x,{id: Math.random().toString(), text: input}])
    console.log(todoValue)
  }

  const connectValues = ()=> {
    let newary = {items:todoValue}
    console.log(newary)
    console.log(newary.items)

  }


  return (
    <div>
      <h2>TodoList</h2>
      <form onSubmit={handleSubmit}>
        <div className="todolistForm" onClick={connectValues}>
        <label className="todoformlabel" ><input type='text' onChange={ e => setInput(e.currentTarget.value)}/></label>
        </div>
      </form>
      <ul>
      {todoValue.map(list => {
        return(
          <li key={list.id}>{list.text}</li>
          )
        })
      }
      </ul>
      <div className="containerCenter" >
        <CheckboxList items={newary}/>
      </div>
    </div>
  )
}

export default TodoList
